var crypto = require('crypto');

// val: encryted data or data that needs encryotion
// mode: encrypt || decrypt
// encryption_key: Key from local server file
crypt = function crypt(val, mode, encryption_key) {

	var algorithm = 'aes256';
	var cipher, decipher;
	var ret;

	if (mode == 'encrypt') {
		cipher = crypto.createCipher(algorithm, encryption_key);
		ret = cipher.update(val, 'utf8', 'hex') + cipher.final('hex');
		return ret;
	} else {
		decipher = crypto.createDecipher(algorithm, encryption_key);
		ret = decipher.update(val, 'hex', 'utf8') + decipher.final('utf8');
		return ret;
	}

}

/* Expose public functions ------ */
exports.crypt = crypt;
